import React, { useState, useRef } from 'react';
import axios from 'axios';
import './styles.css';

function TextSummarizer() {
    const [text, setText] = useState("");
    const [summary, setSummary] = useState("");
    const [loading, setLoading] = useState(false);
    const [isSpeaking, setIsSpeaking] = useState(false);
    const [highlightedWord, setHighlightedWord] = useState("");
    const [isPaused, setIsPaused] = useState(false);
    const [currentIndex, setCurrentIndex] = useState(0);
    const speechRef = useRef(null);

    const handleSummarize = async () => {
        setLoading(true);
        try {
            const response = await axios.post("http://127.0.0.1:5001/summarize", { text });
            setSummary(response.data.summary);
        } catch (error) {
            console.error("Error summarizing text:", error.response || error.message);
            setSummary("Error summarizing text.");
        } finally {
            setLoading(false);
        }
    };

    const handleSpeak = () => {
        if (summary) {
            const utterance = new SpeechSynthesisUtterance(summary);
            utterance.onstart = () => {
                setIsSpeaking(true);
                startTracking(); // Start tracking word index
            };
            utterance.onend = () => {
                setIsSpeaking(false);
                stopTracking(); // Stop tracking when speech ends
            };

            // Set the speechRef to the current utterance
            speechRef.current = utterance;

            // Start speaking the summary
            window.speechSynthesis.speak(utterance);
        }
    };

    const startTracking = () => {
        // Set interval to track the spoken word
        const interval = setInterval(() => {
            const currentWordIndex = getCurrentWordIndex();
            if (currentWordIndex !== currentIndex) {
                setCurrentIndex(currentWordIndex); // Update the current word index
                setHighlightedWord(getWordAt(currentWordIndex)); // Highlight the word
            }
        }, 100);

        // Store the interval ID to stop tracking when speech ends
        speechRef.current.intervalId = interval;
    };

    const stopTracking = () => {
        clearInterval(speechRef.current.intervalId);
    };

    const getCurrentWordIndex = () => {
        const text = summary;
        const currentPosition = window.speechSynthesis.speaking ? speechRef.current?.charIndex || 0 : 0;
        return text.slice(0, currentPosition).split(" ").length - 1;
    };

    const getWordAt = (index) => {
        return summary.split(" ")[index] || "";
    };

    const handlePause = () => {
        if (speechRef.current) {
            window.speechSynthesis.pause();
            setIsPaused(true);
        }
    };

    const handleResume = () => {
        if (speechRef.current) {
            window.speechSynthesis.resume();
            setIsPaused(false);
        }
    };


    return (
        <div className="container">
            <h3>Enter Text for Summarization</h3>
            <textarea
                placeholder="Enter text to summarize"
                value={text}
                onChange={(e) => setText(e.target.value)}
                rows="6"
            ></textarea>
            <button onClick={handleSummarize} disabled={loading || !text}>
                {loading ? "Summarizing..." : "Summarize"}
            </button>

            {summary && (
                <div className="output-box">
                    <h4>Summary:</h4>
                    {/* Split summary into words and highlight the spoken word */}
                    <p>
                        {summary.split(" ").map((word, index) => (
                            <span
                                key={index}
                                className={index === currentIndex ? "highlight" : ""}
                            >
                                {word}{" "}
                            </span>
                        ))}
                    </p>

                    {/* Speak button */}
                    <button onClick={handleSpeak} disabled={isSpeaking || isPaused}>
                        {isSpeaking ? "Speaking..." : "Speak"}
                    </button>

                    {/* Pause and Resume buttons */}
                    {isSpeaking && !isPaused && (
                        <button onClick={handlePause}>Pause</button>
                    )}
                    {isPaused && (
                        <button onClick={handleResume}>Resume</button>
                    )}
                </div>
            )}
        </div>
    );
}

export default TextSummarizer;
